import React, { useEffect, useState } from 'react';

const API = 'http://localhost:5000/api/banner';

const AdminBannerManager = () => {
  const [banners, setBanners] = useState([]);
  const [form, setForm] = useState({ img: '', title: '', link: '' });
  const [editId, setEditId] = useState(null);

  const fetchBanners = async () => {
    const res = await fetch(API);
    const data = await res.json();
    setBanners(data);
  };

  useEffect(() => { fetchBanners(); }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (editId) {
      await fetch(`${API}/${editId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
    } else {
      await fetch(API, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
    }
    setForm({ img: '', title: '', link: '' });
    setEditId(null);
    fetchBanners();
  };

  const handleEdit = banner => {
    setForm({ img: banner.img, title: banner.title || '', link: banner.link || '' });
    setEditId(banner._id);
  };

  const handleDelete = async id => {
    await fetch(`${API}/${id}`, { method: 'DELETE' });
    fetchBanners();
  };

  return (
    <div className="mb-8">
      <h2 className="text-xl font-bold mb-4">Quản lý Banner</h2>
      <form onSubmit={handleSubmit} className="flex gap-2 mb-4">
        <input name="img" value={form.img} onChange={handleChange} placeholder="Link ảnh" className="border p-2 rounded w-1/3" />
        <input name="title" value={form.title} onChange={handleChange} placeholder="Tiêu đề" className="border p-2 rounded w-1/3" />
        <input name="link" value={form.link} onChange={handleChange} placeholder="Link" className="border p-2 rounded w-1/3" />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">{editId ? 'Sửa' : 'Thêm'}</button>
      </form>
      <ul>
        {banners.map((b, idx) => (
          <li key={b._id || b.id || idx} className="flex items-center gap-2 mb-2">
            <img src={b.img} alt="banner" className="w-24 h-12 object-cover rounded" />
            <span>{b.title}</span>
            <a href={b.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">Link</a>
            <button onClick={() => handleEdit(b)} className="bg-yellow-400 px-2 py-1 rounded">Sửa</button>
            <button onClick={() => handleDelete(b._id)} className="bg-red-500 text-white px-2 py-1 rounded">Xoá</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminBannerManager;
