import React, { useEffect, useState } from 'react';

const API = 'http://localhost:5000/api/news';

const AdminNewsManager = () => {
  const [news, setNews] = useState([]);
  const [form, setForm] = useState({ title: '', summary: '', link: '', content: '' });
  const [editId, setEditId] = useState(null);

  const fetchNews = async () => {
    const res = await fetch(API);
    const data = await res.json();
    setNews(data);
  };

  useEffect(() => { fetchNews(); }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (editId) {
      await fetch(`${API}/${editId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
    } else {
      await fetch(API, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
    }
    setForm({ title: '', summary: '', link: '', content: '' });
    setEditId(null);
    fetchNews();
  };

  const handleEdit = item => {
    setForm({ title: item.title, summary: item.summary || '', link: item.link || '', content: item.content || '' });
    setEditId(item._id);
  };

  const handleDelete = async id => {
    await fetch(`${API}/${id}`, { method: 'DELETE' });
    fetchNews();
  };

  return (
    <div className="mb-8">
      <h2 className="text-xl font-bold mb-4">Quản lý Tin tức</h2>
      <form onSubmit={handleSubmit} className="flex gap-2 mb-4">
        <input name="title" value={form.title} onChange={handleChange} placeholder="Tiêu đề" className="border p-2 rounded w-1/4" />
        <input name="summary" value={form.summary} onChange={handleChange} placeholder="Tóm tắt" className="border p-2 rounded w-1/4" />
        <input name="link" value={form.link} onChange={handleChange} placeholder="Link" className="border p-2 rounded w-1/4" />
        <input name="content" value={form.content} onChange={handleChange} placeholder="Nội dung" className="border p-2 rounded w-1/4" />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">{editId ? 'Sửa' : 'Thêm'}</button>
      </form>
      <ul>
        {news.map(n => (
          <li key={n._id} className="mb-2 border-b pb-2">
            <span className="font-bold">{n.title}</span> - {n.summary}
            <a href={n.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline ml-2">Link</a>
            <button onClick={() => handleEdit(n)} className="bg-yellow-400 px-2 py-1 rounded ml-2">Sửa</button>
            <button onClick={() => handleDelete(n._id)} className="bg-red-500 text-white px-2 py-1 rounded ml-2">Xoá</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminNewsManager;
