import React, { useEffect, useState } from 'react';

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [current, setCurrent] = useState(0);

  const visibleCount = 8; // số ảnh hiển thị trong 1 slide

  useEffect(() => {
    fetch('http://localhost:5000/api/gallery')
      .then(res => res.json())
      .then(data => setImages(data))
      .catch(() => setImages([
        { img: '/img/carton.jpg' },
        { img: '/img/carton2.jpg' },
        { img: '/img/carton3.jpg' },
        { img: '/img/carton4.jpg' },
        { img: '/img/bg_1.jpeg' },
        { img: '/img/carton.jpg' },
        { img: '/img/carton2.jpg' },
        { img: '/img/bg_1.jpeg' }
      ]));
  }, []);

  useEffect(() => {
    if (!images.length || images.length <= visibleCount) return;
    const timer = setInterval(() => {
      setCurrent(c => (c >= images.length - visibleCount ? 0 : c + 1));
    }, 3000); // tự động chuyển slide sau 3s
    return () => clearInterval(timer);
  }, [images]);

  if (!images.length) return null;

  const nextSlide = () => {
    setCurrent(c => (c >= images.length - visibleCount ? 0 : c + 1));
  };

  const prevSlide = () => {
    setCurrent(c => (c === 0 ? images.length - visibleCount : c - 1));
  };

  return (
    <section id="gallery" className="py-10 bg-white scroll-mt-20">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-blue-600">Hình Ảnh</h2>
        <div className="gallery-carousel-wrapper relative w-full overflow-hidden rounded-lg shadow-lg h-[300px]">
          <div
            className="gallery-track flex transition-transform duration-700 ease-in-out"
            style={{
              width: `${images.length * 12.5}%`,
              transform: images.length > visibleCount
                ? `translateX(-${current * (100 / images.length)}%)`
                : 'none'
            }}
          >
            {images.map((img, idx) => (
              <div key={idx} className="gallery-slide w-1/8 flex-shrink-0 p-2">
                <img
                  src={img.img}
                  className="rounded-lg shadow-md w-full h-full object-cover"
                  alt={`Sản phẩm ${idx + 1}`}
                />
              </div>
            ))}
          </div>

          {/* Nút điều khiển */}
          {images.length > visibleCount && (
            <>
              <button
                onClick={prevSlide}
                className="absolute top-1/2 left-2 -translate-y-1/2 bg-black/50 text-white p-2 rounded-full hover:bg-black"
              >
                ‹
              </button>
              <button
                onClick={nextSlide}
                className="absolute top-1/2 right-2 -translate-y-1/2 bg-black/50 text-white p-2 rounded-full hover:bg-black"
              >
                ›
              </button>
            </>
          )}
        </div>
      </div>
    </section>
  );
};

export default Gallery;
