import React, { useEffect, useState } from 'react';

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/api/gallery')
      .then(res => res.json())
      .then(data => setImages(data))
      .catch(() => setImages([
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/carton3.jpg'},
        {img: '/img/carton4.jpg'},
        {img: '/img/bg_1.jpeg'},
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/bg_1.jpeg'}
      ]));
  }, []);

  useEffect(() => {
    if (!images.length) return;
    const timer = setInterval(() => {
      setCurrent(c => (c + 1) % images.length);
    }, 4000);
    return () => clearInterval(timer);
  }, [images]);

  if (!images.length) return null;

  return (
    <section id="gallery" className="py-10 bg-white scroll-mt-20">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-blue-600">Hình Ảnh</h2>
        <div className="relative w-full overflow-hidden rounded-lg shadow-lg h-80">
          {images.map((img, idx) => (
            <div key={idx} className={`absolute top-0 left-0 w-full h-full transition-opacity duration-700 ${current===idx?'opacity-100 z-10':'opacity-0 z-0'}`}>
              <img src={img.img} className="rounded-lg shadow-md w-full h-80 object-cover" alt={`Sản phẩm ${idx+1}`} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Gallery;
