import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Gallery = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/gallery')
      .then(res => res.json())
      .then(data => setImages(data))
      .catch(() => setImages([
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/carton3.jpg'},
        {img: '/img/carton4.jpg'},
        {img: '/img/bg_1.jpeg'},
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/bg_1.jpeg'}
      ]));
  }, []);

  return (
    <section id="gallery" className="py-10 bg-white scroll-mt-20">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-blue-600">Hình Ảnh</h2>
        <div className="gallery-carousel-wrapper relative w-full overflow-hidden rounded-lg shadow-lg h-80">
          <div className="gallery-track flex transition-transform duration-500 ease-in-out" style={{width: `${images.length*12.5}%`}}>
            {images.map((img, idx) => (
              <div key={idx} className="gallery-slide w-1/8 flex-shrink-0 p-2">
                <img src={img.img} className="rounded-lg shadow-md w-full h-62 object-cover" alt={`Sản phẩm ${idx+1}`} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Gallery;
