import React, { useEffect, useState } from 'react';

const API = 'http://localhost:5000/api/gallery';
const UPLOAD_API = 'http://localhost:5000/api/gallery/upload';

const AdminGalleryManager = () => {
  const [images, setImages] = useState([]);
  const [form, setForm] = useState({ img: '', caption: '' });
  const [file, setFile] = useState(null);

  const fetchImages = async () => {
    const res = await fetch(API);
    const data = await res.json();
    setImages(data);
  };

  useEffect(() => { fetchImages(); }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    let imgUrl = form.img;
    if (file) {
      const data = new FormData();
      data.append('image', file);
      const res = await fetch(UPLOAD_API, {
        method: 'POST',
        body: data
      });
      const result = await res.json();
      imgUrl = result.url;
    }
    await fetch(API, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ img: imgUrl, caption: form.caption })
    });
    setForm({ img: '', caption: '' });
    setFile(null);
    fetchImages();
  };

  const handleDelete = async id => {
    await fetch(`${API}/${id}`, { method: 'DELETE' });
    fetchImages();
  };

  return (
    <div className="mb-8">
      <h2 className="text-xl font-bold mb-4">Quản lý Hình ảnh</h2>
      <form onSubmit={handleSubmit} className="flex gap-2 mb-4 items-center">
        <input name="img" value={form.img} onChange={handleChange} placeholder="Link ảnh (nếu có)" className="border p-2 rounded w-1/3" />
        <input name="caption" value={form.caption} onChange={handleChange} placeholder="Chú thích" className="border p-2 rounded w-1/3" />
        <input type="file" accept="image/*" onChange={handleFileChange} className="border p-2 rounded w-1/3" />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Thêm</button>
      </form>
      <ul>
        {images.map(img => (
          <li key={img._id} className="flex items-center gap-2 mb-2">
            <img src={img.img} alt="gallery" className="w-24 h-12 object-cover rounded" />
            <span>{img.caption}</span>
            <button onClick={() => handleDelete(img._id)} className="bg-red-500 text-white px-2 py-1 rounded">Xoá</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminGalleryManager;
