import React, { useEffect, useState } from 'react';
import ConfirmDeleteModal from '../components/ConfirmDeleteModal';

const API = 'http://localhost:5000/api/banner';
const UPLOAD_API = 'http://localhost:5000/api/gallery/upload'; // dùng chung API upload ảnh

const AdminBannerManager = () => {
  const [banners, setBanners] = useState([]);
  const [form, setForm] = useState({ img: '', title: '', link: '' });
  const [file, setFile] = useState(null);
  const [editId, setEditId] = useState(null);

  const fetchBanners = async () => {
    const res = await fetch(API);
    const data = await res.json();
    setBanners(data);
  };

  useEffect(() => { fetchBanners(); }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    let imgUrl = form.img;
    if (file) {
      const data = new FormData();
      data.append('image', file);
      const res = await fetch(UPLOAD_API, {
        method: 'POST',
        body: data
      });
      const result = await res.json();
      imgUrl = result.url;
    }
    if (editId) {
      await fetch(`${API}/${editId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ img: imgUrl, title: form.title, link: form.link })
      });
    } else {
      await fetch(API, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ img: imgUrl, title: form.title, link: form.link })
      });
    }
    setForm({ img: '', title: '', link: '' });
    setFile(null);
    setEditId(null);
    fetchBanners();
  };

  const handleEdit = banner => {
    setForm({ img: banner.img, title: banner.title || '', link: banner.link || '' });
    setEditId(banner._id);
  };

  const handleDelete = async id => {
    await fetch(`${API}/${id}`, { method: 'DELETE' });
    fetchBanners();
  };

  return (
    <div className="mb-8">
      <h2 className="text-xl font-bold mb-4">Quản lý Banner</h2>
      <form onSubmit={handleSubmit} className="flex gap-2 mb-4 items-center">
          <input type="file" accept="image/*" onChange={handleFileChange} className="border p-2 rounded w-1/4" required={!editId} />
          <input name="title" value={form.title} onChange={handleChange} placeholder="Tiêu đề" className="border p-2 rounded w-1/4" />
          <input name="link" value={form.link} onChange={handleChange} placeholder="Link" className="border p-2 rounded w-1/4" />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">{editId ? 'Lưu' : 'Thêm'}</button>
      </form>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border rounded shadow">
            <thead>
              <tr className="bg-blue-100">
                <th className="py-2 px-3 border text-center w-16">STT</th>
                <th className="py-2 px-3 border text-center w-40">Ảnh</th>
                <th className="py-2 px-3 border text-left">Tiêu đề</th>
                <th className="py-2 px-3 border text-left">Link</th>
                <th className="py-2 px-3 border text-center w-32">Hành động</th>
              </tr>
            </thead>
            <tbody>
              {banners.map((banner, idx) => (
                <tr key={banner._id || banner.id || idx} className="border-b hover:bg-blue-50">
                  <td className="py-2 px-3 border text-center font-semibold">{idx + 1}</td>
                  <td className="py-2 px-3 border text-center"><img src={banner.img} alt="banner" className="mx-auto w-32 h-16 object-cover rounded shadow" /></td>
                  <td className="py-2 px-3 border align-middle font-bold">{banner.title}</td>
                  <td className="py-2 px-3 border align-middle"><a href={banner.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">{banner.link}</a></td>
                  <td className="py-2 px-3 border text-center">
                    <button onClick={() => handleEdit(banner)} className="bg-yellow-400 px-3 py-1 rounded shadow hover:bg-yellow-500 mr-2">Sửa</button>
                    <button onClick={() => setDeleteId(banner._id)} className="bg-red-500 text-white px-3 py-1 rounded shadow hover:bg-red-600">Xoá</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <ConfirmDeleteModal
          open={!!deleteId}
          onClose={() => setDeleteId(null)}
          onConfirm={handleDelete}
          text="Bạn có chắc chắn muốn xoá banner này không?"
        />
    </div>
  );
};

export default AdminBannerManager;
