import React, { useEffect, useState } from 'react';

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/api/gallery')
      .then(res => res.json())
      .then(data => setImages(data))
      .catch(() => setImages([
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/carton3.jpg'},
        {img: '/img/carton4.jpg'},
        {img: '/img/bg_1.jpeg'},
        {img: '/img/carton.jpg'},
        {img: '/img/carton2.jpg'},
        {img: '/img/bg_1.jpeg'}
      ]));
  }, []);

  useEffect(() => {
    const visibleCount = 4; // Hiển thị 4 ảnh mỗi slide
    if (!images.length || images.length <= visibleCount) return;
    
    const timer = setInterval(() => {
      setCurrent(c => {
        // Khi đến cuối, quay về đầu
        if (c >= images.length - visibleCount) {
          return 0;
        }
        return c + 1;
      });
    }, 3000); // Chậm lại 3 giây mỗi lần chuyển
    
    return () => clearInterval(timer);
  }, [images]);

  if (!images.length) return null;

  const visibleCount = 4;
  const slideWidth = 100 / visibleCount; // 25% cho mỗi ảnh

  return (
    <section id="gallery" className="py-10 bg-white scroll-mt-20">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-blue-600">Hình Ảnh</h2>
        <div className="gallery-carousel-wrapper relative w-full overflow-hidden rounded-lg shadow-lg">
          <div 
            className="gallery-track flex transition-transform duration-1000 ease-in-out"
            style={{
              width: `${(images.length / visibleCount) * 100}%`,
              transform: images.length > visibleCount 
                ? `translateX(-${current * slideWidth}%)` 
                : 'translateX(0%)'
            }}
          >
            {images.map((img, idx) => (
              <div 
                key={idx} 
                className="gallery-slide flex-shrink-0 p-2"
                style={{ width: `${100 / images.length * visibleCount}%` }}
              >
                <img 
                  src={img.img} 
                  className="rounded-lg shadow-md w-full h-60 object-cover" 
                  alt={`Sản phẩm ${idx+1}`} 
                />
              </div>
            ))}
          </div>
        </div>
        
        {/* Thêm dots indicator (tùy chọn) */}
        {images.length > visibleCount && (
          <div className="flex justify-center mt-4 space-x-2">
            {Array.from({ length: Math.ceil(images.length - visibleCount + 1) }).map((_, idx) => (
              <button
                key={idx}
                className={`w-3 h-3 rounded-full transition-colors ${
                  current === idx ? 'bg-blue-600' : 'bg-gray-300'
                }`}
                onClick={() => setCurrent(idx)}
              />
            ))}
          </div>
        )}
      </div>
    </section>
  );
};

export default Gallery;
